// 引入要用的框架
import Vue from 'vue'
// 引入App组件
import App from './App.vue'

// 关闭Vue的生产提示
Vue.config.productionTip = false

// 创建一个 vc 实例对象，并赋给 Demo 变量
const Demo = Vue.extend({})
const d = new Demo()
// 给 Vue.prototype 增加一个变量x，x为一个对象，Vue.prototype 称为 Vue 的原型对象
// Vue.prototype.x = {a:1,b:2}
Vue.prototype.x = d


// 创建vm（vue的实例对象）
new Vue({
    el:'#app',
    render: h => h(App),
    // 用一个生命周期钩子，就不用像上面那样创建一个vc实例对象，直接用 vm
    beforeCreate() {
        console.log(this);
        // Vue.prototype.x = this //安装全局事件总线
        // 把 x 都改为 $bus
        Vue.prototype.$bus = this
    }
})
